import React, { Component } from 'react';
import {  
  View,
  Text,
  Image,
  TouchableHighlight,
  FlatList,
  Share
} from 'react-native';

import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
const WHATSAPP_ICON = "data:image/png;base64,"; 
export default class ItemBox extends Component{

	state={
		picker:'',
		framework:'',
		frameworks:[],
		slider: 0,
		falseSwitchIsOn : false,
		modalVisible : false,
		fill: 50
	}

	compartir=(titulito,mensaje,url) =>{
		Share.share({ 
			message: mensaje, 
			url: url, 
			title: titulito
		}, { 
				dialogTitle: 'Share React Native website', 
				excludedActivityTypes: [ 'com.apple.UIKit.activity.PostToTwitter' ], 
				tintColor: 'green' }) 
		.then(this._showResult) 
		.catch((error) => this.setState({result: 'error: ' + error.message}));
	}


	render(){
		return(
			<View style={{flex:1,flexDirection:'row', borderBottomWidth:0.4}}>

				<View style={{flex:0.5, alignItems:'center', justifyContent:'center'}}>
			        <Image
		        		resizeMode={'cover'}
			          	style={{width: 80, height: 90}}
			        	source={{uri: this.props.imagen}}			          
			          
			        />					
				</View>

				<View style={{flex:2, justifyContent:'center', marginLeft:20}}>
					<Text style={{fontWeight:'bold',fontSize:16}}> {this.props.titulo}</Text>
					<Text> {this.props.descripcion}</Text>
					<Text> {this.props.precio}</Text>
				</View>

				<View style={{flex:0.3, alignItems:'center', justifyContent:'center'}}>
					<TouchableHighlight onPress={()=>this.compartir('',this.props.descripcion,this.props.descripcion)}> 
						<Icon name="share" size={30} color="black" />
					</TouchableHighlight>
				</View>

			</View>
		)
	}
}